import React, { Component } from 'react';
import ListOfBooks  from '../../templateToList/ListOfBooks';

class ListBooks extends Component {
    render() {
        var len = 100;
        var numb = new Array(0);
        var posX = new Array(0);
        var posY = new Array(0);
        var listItems;
        for (var i = 0; i < len; i++){
        numb.push(Math.floor((Math.random() * 2) ));
        posX.push(Math.floor((Math.random() * 90) + 1)+'%');
        posY.push(Math.floor((Math.random() * 1400) + 1));
        }
        listItems = numb.map((numb, x) =>
        <li className="numbers" style={{left: posX[x], bottom: posY[x]}}>{numb}</li>
        );
        return (
            <div className="backgroundBooks">
                <ul>{listItems}</ul>
                <h1 className="TextToSegmentBooks">My favourite books</h1>
                <div className="ContainerForListsOfBooks">
                    {ListOfBooks.map((project) => {
                        return (
                        <div key={project.id} className="boxOfListOfBooks" id={project.id}>
                            <div className="containerOfBooks" >
                                <div className="imgAndTextOfBooks" >
                                    <a href={project.linkToAlbum} target="_blank"><img src={project.imgOfProject} className="imgListOfBooks"/></a>
                                    <div className="bottom-left" style={{color:project.color}}><h5 style={{fontFamily: project.fontFamily}}>{project.NameOfBook}</h5></div>
                                </div>
                            </div>
                        </div>
                        )
                        })}
                </div>
                {/* <h1 className="TextToSegmentBooks">Rest of good books</h1>
                <div className="ContainerForListsOfBooks">
                    {ListRestOfGMusic.map((project) => {
                        return (
                        <div key={project.id} className="barOfListOfBooks" id={project.id}>
                                <div className="TextOfBooksBar" style={{color:"#fff"}}>
                                    <FaYoutube style={{fontSize:"50px", float: "left", marginLeft: "25px"}}/>
                                    <div className="titleBooks">{project.NameOfProject}</div>
                                    <div className="authorBooks">{project.NameOfProject}</div>
                                </div>
                        </div>
                        )
                        })}
                </div> */}
            </div>
        );
    }
}

export default ListBooks;
